@SuppressLint("UnusedMaterial3ScaffoldPaddingParameter")
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun CategoryScreen(navHostController: NavHostController) {
    Scaffold(
        modifier = Modifier.fillMaxSize(),
        containerColor = colorResource(id = R.color.black),
    ) {
        Column() {
            Text(
                text = "Categories",
                fontSize = 30.sp,
                color = Color.White,
                fontWeight = FontWeight.Bold,
                modifier = Modifier.padding(top = 20.dp, start = 10.dp),
            )
            Spacer(modifier = Modifier.padding(top = 20.dp))
            CategoryListInitilizer()
        }
    }
}

@Composable
private fun CategoryListInitilizer() {
    val categoryList = CategoriesList()

    LazyColumn {
        items(categoryList) {
            AddingCategoryValues(category = it)
        }
    }
}

@OptIn(ExperimentalGlideComposeApi::class)
@Composable
private fun AddingCategoryValues(category: CategoriesDto) {
    Card(
        modifier = Modifier.padding(top = 14.dp, start = 10.dp, end = 10.dp, bottom = 10.dp)
            .fillMaxWidth().height(120.dp).clip(
                RoundedCornerShape(12.dp),
            ),

    ) {
        Box() {
            GlideImage(
                model = category.imgUrl,
                contentDescription = "image",
                contentScale = ContentScale.Crop,
                modifier = Modifier.graphicsLayer(alpha = 90.5f).alpha(90.5f),
            ) {
                it.load(category.imgUrl)
            }

            Text(
                text = category.title,
                modifier = Modifier.fillMaxWidth().fillMaxHeight().padding(10.dp),
                fontWeight = FontWeight.Bold,
                fontSize = 60.sp,
                color = colorResource(id = R.color.white),
                textAlign = TextAlign.Center,
            )
        }
    }
}

@Composable
private fun CategoriesList(): List<CategoriesDto> {
    return listOf(
        CategoriesDto("horror", HORROR_IMG),
        CategoriesDto("crime", CRIME_IMG),
        CategoriesDto("superhero", SUPER_HERO_IMG),
        CategoriesDto("romance", ROMANCE_IMG),
        CategoriesDto("action", ACTION_IMG),
        CategoriesDto("comedy", COMEDY_IMG),
        CategoriesDto("drama", DRAMA_IMG),
        CategoriesDto("adventure", ADVENTURE_IMG),
    )
}

